templates.source:
query {
	hero(episode: JEDI) {
		id
		appearsIn
	}
	reviews(episode: EMPIRE || JEDI) {
		stars
		commentary
		time
	}
}

query {
	hero(episode: EMPIRE || JEDI) {
		id
		name
		appearsIn
		friends {
			id
			name
			appearsIn
			friends {
				id
				name
				appearsIn
			}
		}
		friendsConnection(first: > 0, after: len > 0 || null) {
			totalCount
			friends {
				id
				name
				appearsIn
			}
		}
	}
}

query {
	hero(episode: EMPIRE || JEDI) {
		id
		name
		appearsIn
		friends {
			id
			name
			appearsIn
			friends {
				id
				name
				appearsIn
			}
		}
	}
}

query {
	hero(episode: EMPIRE) {
		id
		name
		... on Droid { primaryFunction }
		... on Human { height(unit: *) mass }
		appearsIn
		friendsConnection {
			totalCount
			friends {
				id name appearsIn
				...on Droid { primaryFunction }
				... on Human { height(unit: *) mass }
			}
		}
	}
	starship(id:"3001") { name length(unit: != METER) }
}

query {
	hero(episode: *) {
		id
		name
		friends {
			id
			name
		}
	}
}

query {
	hero(episode: JEDI) {
		id
		name
		appearsIn
		friends {
			id
			name
			appearsIn
		}
	}
	reviews(episode: EMPIRE || JEDI) {
		stars
		commentary
		time
	}
}

query {
	human(id: *) {
		max 2 {
			id
			name
			appearsIn
		}
	}
}
